import React, { useState } from "react";
import Editor from "@monaco-editor/react";
import Visualizer from "./Visualizer";

export default function App() {
  const [code, setCode] = useState("// Write JS code here\nconsole.log('Hello, Visualizer!');");
  const [logs, setLogs] = useState([]);

  const runCode = () => {
  const capturedLogs = [];
  const originalLog = console.log;
  const originalSetTimeout = window.setTimeout;

  // Intercept console.log
  console.log = (...args) => {
    capturedLogs.push(args.join(" "));
    originalLog(...args);
  };

  // Intercept setTimeout
  window.setTimeout = (callback, delay, ...args) => {
    capturedLogs.push(`üïì setTimeout scheduled: ${delay}ms`);
    return originalSetTimeout(() => {
      capturedLogs.push(`‚úÖ setTimeout executed after ${delay}ms`);
      callback(...args);
      setLogs([...capturedLogs]); // update logs after async
    }, delay);
  };

  try {
    // eslint-disable-next-line no-eval
    eval(code);
  } catch (err) {
    capturedLogs.push("‚ùå Error: " + err.message);
  }

  // Restore originals
  console.log = originalLog;
  window.setTimeout = originalSetTimeout;

  setLogs([...capturedLogs]);
};

  return (
    <div className="app">
      <div className="editor">
        <Editor
          height="300px"
          defaultLanguage="javascript"
          defaultValue={code}
          onChange={(value) => setCode(value)}
        />
        <button onClick={runCode} className="run-btn">Run Code</button>
      </div>
      <Visualizer logs={logs} />
    </div>
  );
}
