import React, { useEffect, useState } from "react";
import "./MemoryVisualizer.css";

export default function MemoryVisualizer({ memoryEvents = [] }) {
  const [visibleEvents, setVisibleEvents] = useState([]);

  useEffect(() => {
    if (!memoryEvents || memoryEvents.length === 0) return;

    setVisibleEvents([]);
    let i = 0;

    const interval = setInterval(() => {
      setVisibleEvents((prev) => [...prev, memoryEvents[i]]);
      i++;
      if (i >= memoryEvents.length) clearInterval(interval);
    }, 500); // show each event every 500ms

    return () => clearInterval(interval);
  }, [memoryEvents]);

  const callStackItems = [];
  const microtasks = [];
  const macrotasks = [];
  const heapItems = [];

  visibleEvents.forEach((e) => {
    if (e.type === "callstack-push") callStackItems.push(e.value);
    else if (e.type === "callstack-pop") callStackItems.pop();
    else if (e.type === "microtask") microtasks.push(e.value);
    else if (e.type === "macrotask") macrotasks.push(e.value);
    else if (e.type === "heap") heapItems.push(e.value);
  });

  return (
    <div className="memory-layout">
      <div className="top-section">
        <div className="memory-block call-stack">
          <h4>ðŸ“ž Call Stack</h4>
          <ul>
            {[...callStackItems].reverse().map((item, idx) => (
              <li key={idx} className="callstack-item">{item}</li>
            ))}
          </ul>
        </div>

        <div className="memory-block microtask">
          <h4>ðŸ¦  Microtasks Queue</h4>
          <ul>{microtasks.map((m, i) => <li key={i}>{m}</li>)}</ul>
        </div>

        <div className="memory-block macrotask">
          <h4>ðŸ•’ Macrotasks Queue</h4>
          <ul>{macrotasks.map((m, i) => <li key={i}>{m}</li>)}</ul>
        </div>
      </div>

      <div className="heap-block">
        <h4>ðŸ§  Heap (Memory)</h4>
        <ul>{heapItems.map((h, i) => <li key={i}>{h}</li>)}</ul>
      </div>
    </div>
  );
}
