import React from "react";
import { Outlet } from "react-router-dom";
import { useCallStack } from "./CallStackContext";

const Layout = () => {
  const { callStack } = useCallStack();

  return (
    <div className="flex h-screen">
      {/* Left Panel for routes */}
      <div className="w-1/3 border-r p-4 bg-gray-100 overflow-auto">
        <h2 className="font-bold text-lg mb-2">Page Area</h2>
        <Outlet />
      </div>

      {/* Middle Panel: Placeholder for Visualizer */}
      <div className="w-1/3 border-r p-4 bg-white">
        <h2 className="font-bold text-lg mb-2">Visualizer</h2>
        <p className="text-sm text-gray-500">This could show animations or variable states.</p>
      </div>

      {/* Right Panel: Call Stack */}
      <div className="w-1/3 p-4 bg-slate-100 overflow-auto">
        <h2 className="font-bold text-lg mb-2">Call Stack</h2>
        <ul>
          {callStack.map((item, idx) => (
            <li key={idx} className="bg-white p-2 rounded shadow mb-1">{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Layout;
