import React, { useState } from "react";
import Editor from "@monaco-editor/react";
import "./EditorPage.css";
import { useCallStack } from "./CallStackContext";

export default function EditorPage() {
  const [code, setCode] = useState(`const a = track(function a() {
  b();
}, "a");

const b = track(function b() {
  console.log("Inside b");
  setTimeout(() => {
    console.log("setTimeout done");
  }, 0);

  Promise.resolve().then(() => {
    console.log("Promise resolved");
  });

  const obj = { message: "hello" }; // heap
  console.log("Heap object created", obj);
}, "b");

a();`);

  const [logs, setLogs] = useState([]);
  const [callStackLogs, setCallStackLogs] = useState([]);
  const [executionSpeed, setExecutionSpeed] = useState(500);
  const { callStack, setCallStack } = useCallStack();

  const delay = (ms) => new Promise((res) => setTimeout(res, ms));

  const runCode = async () => {
    const capturedLogs = [];
    const capturedStackLogs = [];
    const newCallStack = [];

    const originalLog = console.log;
    console.log = (...args) => {
      const message = args.map(arg =>
        typeof arg === 'object' ? JSON.stringify(arg) : String(arg)
      ).join(" ");
      capturedLogs.push(message);

      if (message.startsWith(">> Enter:")) {
        const fn = message.split(":")[1].trim();
        newCallStack.push({ type: "enter", fn });
        capturedStackLogs.push({ type: "enter", fn });
      } else if (message.startsWith("<< Exit:")) {
        const fn = message.split(":")[1].trim();
        newCallStack.push({ type: "exit", fn });
        capturedStackLogs.push({ type: "exit", fn });
      }
    };

    try {
      const track = (fn, name) => {
        return function (...args) {
          console.log(`>> Enter: ${name}`);
          const result = fn.apply(this, args);
          console.log(`<< Exit: ${name}`);
          return result;
        };
      };

      const AsyncFunction = Object.getPrototypeOf(async function () { }).constructor;
      const wrappedCode = new AsyncFunction("track", code);
      await wrappedCode(track);
    } catch (e) {
      capturedLogs.push(`Error: ${e.message}`);
    }

    console.log = originalLog;

    setLogs(capturedLogs);
    setCallStackLogs(capturedStackLogs);

    // Animate the call stack
    for (let i = 0; i < newCallStack.length; i++) {
      const step = newCallStack[i];
      setCallStack((prev) => {
        if (step.type === "enter") return [...prev, step.fn];
        if (step.type === "exit") return prev.slice(0, -1);
        return prev;
      });
      await delay(executionSpeed);
    }
  };

  return (
    <div className="editor-page">
      <div className="editor-section">
        <Editor
          height="400px"
          language="javascript"
          theme="vs-dark"
          value={code}
          onChange={(value) => setCode(value)}
        />
        <div className="controls">
          <button className="run-btn" onClick={runCode}>▶️ Run</button>
          <div className="slider">
            <label>⏱ Speed:</label>
            <input
              type="range"
              min="100"
              max="2000"
              value={executionSpeed}
              onChange={(e) => setExecutionSpeed(Number(e.target.value))}
            />
          </div>
        </div>
      </div>

      <div className="output-layout">
        <div className="logs-panel">
          <h3>Console Logs</h3>
          <pre>{logs.join("\n")}</pre>
        </div>

        <div className="callstack-panel">
          <h3>Call Stack</h3>
          <ul>
            {callStack.map((fn, idx) => (
              <li key={idx} className="enter">🔹 {fn}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
