import React from "react";
import { Outlet } from "react-router-dom";
import { useCallStack } from "./components/CallStackContext";



const Layout = () => {
  const { callStack } = useCallStack(); // ✅ Use the context hook

  return (
    <div className="flex h-screen">
      <div className="w-1/3 border-r p-4 bg-gray-100">
        <h2 className="font-bold text-lg mb-2">Editor</h2>
        <Outlet />
      </div>
      <div className="w-1/3 border-r p-4 bg-white">
        <h2 className="font-bold text-lg mb-2">Visualizer</h2>
        {/* Maybe add something later */}
      </div>
      <div className="w-1/3 p-4 bg-slate-100">
        <h2 className="font-bold text-lg mb-2">Call Stack</h2>
        <ul>
          {callStack.map((item, idx) => (
            <li key={idx} className="bg-white p-2 rounded shadow mb-1">{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Layout;
