import React, { useState } from "react";
import Editor from "@monaco-editor/react";
import "./EditorPage.css";

export default function EditorPage() {
  const [code, setCode] = useState(`const a = track(function a() {
  b();
}, "a");

const b = track(function b() {
  console.log("Inside b");
}, "b");

a();`);

  const [logs, setLogs] = useState([]);
  const [callStackLogs, setCallStackLogs] = useState([]);

  const runCode = () => {
    const capturedLogs = [];
    const capturedStackLogs = [];

    // Override console.log
    const originalLog = console.log;
    console.log = (...args) => {
      const message = args.map(arg =>
        typeof arg === 'object' ? JSON.stringify(arg) : String(arg)
      ).join(" ");
      capturedLogs.push(message);

      if (message.startsWith(">> Enter:") || message.startsWith("<< Exit:")) {
        capturedStackLogs.push(message);
      }

      originalLog(...args);
    };

    // Add instrumented code
    const instrumentedCode = `
      function track(fn, name) {
        return function (...args) {
          console.log(">> Enter:", name);
          const result = fn.apply(this, args);
          console.log("<< Exit:", name);
          return result;
        };
      }

      ${code}
    `;

    try {
      const fn = new Function(instrumentedCode);
      fn();
    } catch (err) {
      capturedLogs.push("Error: " + err.message);
    }

    console.log = originalLog;
    setLogs(capturedLogs);
    setCallStackLogs(capturedStackLogs);
  };

  return (
    <div className="editor-page">
      <div className="editor-section">
        <Editor
          height="300px"
          language="javascript"
          value={code}
          onChange={(val) => setCode(val)}
        />
        <button onClick={runCode} className="run-btn">Run Code</button>
      </div>

      <div className="output-layout">
        <div className="logs-panel">
          <h3>Console Logs</h3>
          <pre>{logs.join("\n")}</pre>
        </div>

        <div className="callstack-panel">
          <h3>Call Stack Trace</h3>
          <ul>
            {callStackLogs.map((entry, idx) => (
              <li key={idx} className={entry.startsWith(">>") ? "enter" : "exit"}>
                {entry}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
