import React from "react";
import "./MemoryVisualizer.css";

export default function MemoryVisualizer({ callStack }) {
  return (
    <div className="memory-layout">
      <div className="top-section">
        <div className="memory-block call-stack">
          <h4>ðŸ“ž Call Stack</h4>
          <ul>
            {[...callStack].reverse().map((item, idx) => (
              <li key={idx} className={item.startsWith(">>") ? "enter" : "exit"}>
                {item.replace(">> Enter: ", "").replace("<< Exit: ", "")}
              </li>
            ))}
          </ul>
        </div>

        <div className="memory-block microtask">
          <h4>ðŸ¦  Microtasks Queue</h4>
          <p className="placeholder">Promise callbacks</p>
        </div>

        <div className="memory-block macrotask">
          <h4>ðŸ•’ Macrotasks Queue</h4>
          <p className="placeholder">setTimeout, setInterval</p>
        </div>
      </div>

      <div className="heap-block">
        <h4>ðŸ§  Heap (Memory)</h4>
        <p className="placeholder">Allocated objects/functions</p>
      </div>
    </div>
  );
}
