import React, { useState } from "react";
import Editor from "@monaco-editor/react";

const EditorPage = () => {
  const [code, setCode] = useState(`console.log("Start");
setTimeout(() => {
  console.log("Macrotask: Timeout");
}, 0);

Promise.resolve().then(() => {
  console.log("Microtask: Promise 1");
});

Promise.resolve().then(() => {
  console.log("Microtask: Promise 2");
});

console.log("End");`);

  const [callStack, setCallStack] = useState([]);
  const [microtasks, setMicrotasks] = useState([]);
  const [macrotasks, setMacrotasks] = useState([]);
  const [consoleOutput, setConsoleOutput] = useState([]);
  const [heap, setHeap] = useState([]);

  const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

  const runCodeWithSimulation = async () => {
    setCallStack([]);
    setMicrotasks([]);
    setMacrotasks([]);
    setConsoleOutput([]);
    setHeap([]);

    const pushToCallStack = async (item) => {
      setCallStack((prev) => [...prev, item]);
      await delay(500);
    };

    const popCallStack = async () => {
      setCallStack((prev) => prev.slice(0, -1));
      await delay(500);
    };

    const log = async (msg) => {
      setConsoleOutput((prev) => [...prev, msg]);
      await delay(500);
    };

    await pushToCallStack("console.log: Start");
    await log("Start");
    await popCallStack();

    setMacrotasks(["setTimeout callback"]);
    setMicrotasks(["Promise 1", "Promise 2"]);

    await pushToCallStack("console.log: End");
    await log("End");
    await popCallStack();

    await delay(1000);

    for (let task of microtasks) {
      await pushToCallStack(`Microtask: ${task}`);
      await log(task);
      await popCallStack();
    }

    await delay(500);

    for (let task of macrotasks) {
      await pushToCallStack(`Macrotask: ${task}`);
      await log("Macrotask: Timeout");
      await popCallStack();
    }
  };

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px", padding: 20 }}>
      <div>
        <Editor
          height="400px"
          defaultLanguage="javascript"
          defaultValue={code}
          theme="vs-dark"
          onChange={(value) => setCode(value)}
        />
        <button onClick={runCodeWithSimulation} style={{ marginTop: 10 }}>Run</button>
      </div>
      <div style={{ display: "flex", flexDirection: "column", gap: 10 }}>
        <div>
          <h4>Call Stack</h4>
          <ul>
            {callStack.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
        <div>
          <h4>Microtasks</h4>
          <ul>
            {microtasks.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
        <div>
          <h4>Macrotasks</h4>
          <ul>
            {macrotasks.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
        <div>
          <h4>Console Output</h4>
          <ul>
            {consoleOutput.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default EditorPage;
