import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import { executeCodeWithDelay } from './interpreter'; // Make sure this file exists
import MemoryVisualizer from './MemoryVisualizer';
import './styles.css';

const defaultCode = `function main() {
  console.log("Start");

  setTimeout(() => {
    console.log("Inside setTimeout");
  }, 0);

  Promise.resolve().then(() => {
    console.log("Inside Promise");
  });

  console.log("End");
}

main();`;

function EditorPage() {
  const [code, setCode] = useState(defaultCode);
  const [consoleOutput, setConsoleOutput] = useState([]);
  const [callStack, setCallStack] = useState([]);
  const [microtasks, setMicrotasks] = useState([]);
  const [macrotasks, setMacrotasks] = useState([]);
  const [delay, setDelay] = useState(500);

  const handleRunCode = () => {
    setConsoleOutput([]);
    setCallStack([]);
    setMicrotasks([]);
    setMacrotasks([]);
    executeCodeWithDelay(code, {
      setConsoleOutput,
      setCallStack,
      setMicrotasks,
      setMacrotasks,
      delay
    });
  };

  return (
    <div className="editor-container">
      <h2>üß† JS Visualizer with Memory & Call Stack</h2>

      <div style={{ marginBottom: '10px' }}>
        <label htmlFor="delay">‚öôÔ∏è Execution Delay (ms): </label>
        <input
          id="delay"
          type="number"
          value={delay}
          onChange={(e) => setDelay(Number(e.target.value))}
          style={{ width: '60px', marginLeft: '5px' }}
        />
      </div>

      <Editor
        height="300px"
        defaultLanguage="javascript"
        value={code}
        onChange={(value) => setCode(value)}
        theme="vs-dark"
      />

      <button onClick={handleRunCode} style={{ marginTop: '10px' }}>‚ñ∂Ô∏è Run Code</button>

      <div className="visualizer-column-layout">
        <div className="console-output">
          <h3>üñ•Ô∏è Console Output</h3>
          <div className="console-box">
            {consoleOutput.map((line, index) => (
              <div key={index}>{line}</div>
            ))}
          </div>
        </div>

        <MemoryVisualizer
          callStack={callStack}
          microtasks={microtasks}
          macrotasks={macrotasks}
        />
      </div>
    </div>
  );
}

export default EditorPage;
