import React, { useState } from "react";
import Editor from "@monaco-editor/react";
import MemoryVisualizer from "./MemoryVisualizer";

const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const defaultCode = `function main() {
  console.log("Start");

  setTimeout(() => {
    console.log("Inside setTimeout");
  }, 0);

  Promise.resolve().then(() => {
    console.log("Inside Promise");
  });

  console.log("End");
}

main();`;

const EditorPage = () => {
  const [code, setCode] = useState(defaultCode);
  const [consoleOutput, setConsoleOutput] = useState([]);
  const [callStack, setCallStack] = useState([]);
  const [microtasks, setMicrotasks] = useState([]);
  const [macrotasks, setMacrotasks] = useState([]);
  const [heap, setHeap] = useState([]);
  const [delayMs, setDelayMs] = useState(500);

  const clearAll = () => {
    setConsoleOutput([]);
    setCallStack([]);
    setMicrotasks([]);
    setMacrotasks([]);
    setHeap([]);
  };

  const runCodeWithSlowExecution = async () => {
  clearAll();
  setConsoleOutput((prev) => [...prev, "Starting execution..."]);
  await delay(delayMs);

  setCallStack(["main()"]);
  setConsoleOutput((prev) => [...prev, "Call Stack: main()"]);
  await delay(delayMs);

  const lines = code.split("\n");
  console.log(code)

  for (let line of lines) {
    const trimmed = line.trim();
    console.log(line)

    if (trimmed.startsWith("console.log")) {
      const logMatch = trimmed.match(/console\.log\((.*)\)/);
      if (logMatch) {
  try {
    const evaluated = eval(logMatch[1]); // Safely evaluates the expression
    setConsoleOutput((prev) => [
      ...prev,
      `console.log: ${JSON.stringify(evaluated)}`,
    ]);
  } catch (e) {
    setConsoleOutput((prev) => [
      ...prev,
      `console.log: Error evaluating ${logMatch[1]}`,
    ]);
  }
  await delay(delayMs);
}

    } else if (trimmed.startsWith("setTimeout")) {
      setMacrotasks((prev) => [...prev, "setTimeout callback"]);
      setConsoleOutput((prev) => [...prev, "setTimeout() moved to Macrotask queue"]);
      await delay(delayMs);
    } else if (trimmed.includes("Promise.resolve().then")) {
      setMicrotasks((prev) => [...prev, "Promise.then"]);
      setConsoleOutput((prev) => [...prev, "Promise.then() moved to Microtask queue"]);
      await delay(delayMs);
    }
  }

  setCallStack([]);
  setConsoleOutput((prev) => [...prev, "Call Stack empty"]);
  await delay(delayMs);

  // Execute Microtasks
  if (microtasks.length) {
    setConsoleOutput((prev) => [...prev, "Executing Microtasks..."]);
    setCallStack(["Promise.then"]);
    setConsoleOutput((prev) => [...prev, "console.log: Inside Promise"]);
    await delay(delayMs);
    setMicrotasks([]);
    setCallStack([]);
    setConsoleOutput((prev) => [...prev, "Microtasks done"]);
    await delay(delayMs);
  }

  // Execute Macrotasks
  if (macrotasks.length) {
    setConsoleOutput((prev) => [...prev, "Executing Macrotasks..."]);
    setCallStack(["setTimeout callback"]);
    setConsoleOutput((prev) => [...prev, "console.log: Inside setTimeout"]);
    await delay(delayMs);
    setMacrotasks([]);
    setCallStack([]);
  }

  setConsoleOutput((prev) => [...prev, "Execution Complete ‚úÖ"]);
};

  return (
    <div style={{ padding: "20px" }}>
  <h2>üß† JS Visualizer with Memory & Call Stack</h2>

  {/* Execution Delay Config */}
  <div style={{ marginBottom: "10px" }}>
    <label>
      ‚è± Execution Delay (ms):{" "}
      <input
        type="number"
        value={delayMs}
        onChange={(e) => setDelayMs(Number(e.target.value))}
        style={{ width: "80px", marginLeft: "5px" }}
      />
    </label>
  </div>

  {/* üî≤ 2x2 Grid Layout with Left side 50% */}
  <div
    style={{
      display: "grid",
      gridTemplateColumns: "50% 50%",
      gridTemplateRows: "auto auto",
      gap: "20px",
    }}
  >
    {/* Top-Left: Code Editor */}
    <div style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "10px" }}>
      <Editor
        height="400px"
        width="100%"
        defaultLanguage="javascript"
        value={code}
        onChange={(value) => setCode(value)}
        theme="vs-dark"
      />
      <button onClick={runCodeWithSlowExecution} style={{ marginTop: "10px" }}>
        ‚ñ∂Ô∏è Run Code
      </button>
    </div>

    {/* Top-Right: Memory Visualizer */}
    <div style={{ border: "1px solid #ccc", borderRadius: "8px", padding: "10px" }}>
      <h3 style={{ fontSize: "20px", marginBottom: "15px" }}>üß† Call Stack + Task Queues</h3>
      <div
        style={{
          padding: "15px",
          borderRadius: "10px",
          minHeight: "400px",
          fontSize: "16px",
          border: "2px solid #444",
        }}
      >
        <MemoryVisualizer
          callStack={callStack}
          microtasks={microtasks}
          macrotasks={macrotasks}
          heap={heap}
        />
      </div>
    </div>

    {/* Bottom-Left: Console Output */}
    <div
      style={{
        backgroundColor: "#000",
        color: "#0f0",
        padding: "15px",
        borderRadius: "8px",
        fontFamily: "monospace",
        minHeight: "200px",
        overflowY: "auto",
        border: "1px solid #333",
      }}
    >
      <h3 style={{ color: "#fff" }}>üìã Console Output</h3>
      {consoleOutput.map((line, idx) => (
        <div key={idx}>{line}</div>
      ))}
    </div>

    {/* Bottom-Right: Placeholder */}
    {/* Bottom-Right: Console Output Log */}
{/* Bottom-Right: Console Output Log */}
<div style={{
  backgroundColor: "#1e1e1e",
  color: "#0f0",
  padding: "10px",
  borderRadius: "5px",
  fontFamily: "monospace",
  border: "1px solid #444",
  minHeight: "200px",
  overflowY: "auto"
}}>
  <h3 style={{ color: "#fff" }}>üìã Logs</h3>
  {
    consoleOutput.filter(line => line.startsWith("console.log")).length === 0 ? (
      <div style={{ color: "#777" }}>No logs yet. Run the code!</div>
    ) : (
      consoleOutput
        .filter(line => line.startsWith("console.log"))
        .map((line, idx) => (
          <div key={idx}>{line.replace("console.log: ", "")}</div>
        ))
    )
  }
</div>



  </div>
</div>
  );
};

export default EditorPage;
